
package deno;

public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
	   char grade='S'; // implicit type casting
	   int x=grade;
		long y=x;
		float z=y;
		System.out.println(x);
         System.out.println(y);
         System.out.println(z);
	}

}
